	(1671, '文章模块', '上传', '/admin/articles/images', 'com.aurora.controller.ArticleController.saveArticleImages', '上传文章图片', 'file', 'POST', '{"code":20000,"data":"http://150.158.199.226:9000/avatar/aurora/articles/a750e978055d5f07a50deafbf7a14eea.png","flag":true,"message":"操作成功"}', 1, '演示账号', '127.0.0.1', '内网IP|内网IP', '2024-04-30 14:04:43', NULL),
	(1672, '文章模块', '新增或修改', '/admin/articles', 'com.aurora.controller.ArticleController.saveOrUpdateArticle', '保存和修改文章', '[{"articleAbstract":"","articleContent":"　讲述小演员毕超（包贝尔 饰）因意外失忆误以为自己是个十恶不赦的大反派，还把与自己对戏的女演员冰冰（克拉拉 饰）也错认成了现实中的妻子（李嘉琦 饰），为了让妻子过上更好的生活，毕超下定决心完成反派“绑架大富豪”的使命，在这一过程中意外与老大（周大勇 饰）、老二（马旭东 饰）、老三（喳喳 饰）三名歹徒结下梁子，自此踏上了惨遭碾压的倒霉爆笑之旅。","articleCover":"http://150.158.199.226:9000/avatar/aurora/articles/a750e978055d5f07a50deafbf7a14eea.png","articleTitle":"大“反”派 ","categoryName":"分类","isFeatured":1,"isTop":1,"status":1,"tagNames":["你好"],"type":1}]', 'POST', '{"code":20000,"flag":true,"message":"操作成功"}', 1, '演示账号', '127.0.0.1', '内网IP|内网IP', '2024-04-30 14:04:48', NULL),
	(1673, '文章模块', '上传', '/admin/articles/images', 'com.aurora.controller.ArticleController.saveArticleImages', '上传文章图片', 'file', 'POST', '{"code":20000,"data":"http://150.158.199.226:9000/avatar/aurora/articles/a750e978055d5f07a50deafbf7a14eea.png","flag":true,"message":"操作成功"}', 1, '演示账号', '127.0.0.1', '内网IP|内网IP', '2024-04-30 14:05:31', NULL),
	(1674, '文章模块', '新增或修改', '/admin/articles', 'com.aurora.controller.ArticleController.saveOrUpdateArticle', '保存和修改文章', '[{"articleAbstract":"","articleContent":"文章曾是一位备受瞩目的青年演员，曾经演出过多部经典作品，并且和马伊琍的姐弟恋婚姻也一直是人们津津乐道的话题。本应该成为事业和爱情双丰收的赢家，然而由于一次错误的选择，他被演艺圈封杀了，婚姻也在几年的残喘后结束了。他离开娱乐圈后，走向了两个极端。\\n最近，有网友上传了一段文章的视频，引起了","articleCover":"http://150.158.199.226:9000/avatar/aurora/articles/a750e978055d5f07a50deafbf7a14eea.png","articleTitle":"原创 38岁文章近况曝光，满头白发眼神呆滞，和马伊琍状态相差大","categoryName":"分类","isFeatured":1,"isTop":1,"status":1,"tagNames":[],"type":1}]', 'POST', '{"code":20000,"flag":true,"message":"操作成功"}', 1, '演示账号', '127.0.0.1', '内网IP|内网IP', '2024-04-30 14:05:40', NULL),
	(1675, '文章模块', '上传', '/admin/articles/images', 'com.aurora.controller.ArticleController.saveArticleImages', '上传文章图片', 'file', 'POST', '{"code":20000,"data":"http://150.158.199.226:9000/avatar/aurora/articles/7a59b6752c12cfcf1e5f2739ca978096.jpg","flag":true,"message":"操作成功"}', 1, '演示账号', '127.0.0.1', '内网IP|内网IP', '2024-04-30 14:07:13', NULL),
	(1676, '文章模块', '新增或修改', '/admin/articles', 'com.aurora.controller.ArticleController.saveOrUpdateArticle', '保存和修改文章', '[{"articleAbstract":"","articleContent":"文章曾是一位十分有实力青年演员，演过很多经典的作品，和马伊琍的姐弟恋婚姻也一直被津津乐道，然而本应该是事业爱情双丰收的人生赢家，却因为走错一步，被演艺圈封杀，两人的婚姻也残喘了几年后走到了终点。两人离婚后的状态，好像走向了两个极端，","articleCover":"http://150.158.199.226:9000/avatar/aurora/articles/7a59b6752c12cfcf1e5f2739ca978096.jpg","articleTitle":"原创 38岁文章近况曝光，模样大变满头白发，前妻马伊琍状态却越来越好","categoryName":"分类","isFeatured":0,"isTop":0,"status":1,"tagNames":[],"type":1}]', 'POST', '{"code":20000,"flag":true,"message":"操作成功"}', 1, '演示账号', '127.0.0.1', '内网IP|内网IP', '2024-04-30 14:07:19', NULL),
