INSERT INTO `t_talk` (`id`, `user_id`, `content`, `images`, `is_top`, `status`, `create_time`, `update_time`) VALUES
	(68, 1, '文章曾是一位备受瞩目的青年演员，曾经演出过多部经典作品，并且和马伊琍的姐弟恋婚姻也一直是人们津津乐道的话题。本应该成为事业和爱情双丰收的赢家，然而由于一次错误的选择，他被演艺圈封杀了，婚姻也在几年的残喘后结束了。他离开娱乐圈后，走向了两个极端。', '["http://150.158.199.226:9000/avatar/aurora/talks/1f99eecc2390d7f5e6c92f7b57099497.jpg"]', 0, 1, '2024-04-30 17:16:54', NULL),
	(69, 1, '近日，有网友上传了一段文章的近期视频，文章的状态变化之大，引发网友热议。视频中，文章一身黑色西装坐在台上，已经久未露面的他看起来，身材消瘦， 面色暗淡，脸上多了不少皱纹，面部表情看起来有点尴尬，目光呆滞无光，早已经没有当年的意气风发。', '["http://150.158.199.226:9000/avatar/aurora/talks/bb307cfffffe16167d5b1ce6f916aaa4.png"]', 0, 1, '2024-04-30 17:17:14', NULL),
	(70, 1, '最引人注意的是，文章满头白发，看起来十分苍老。值得注意的是，文章才38岁，正值青壮之年，怎么可能会有这么多白发呢？但是，有网友表示可能是染的，因为他在话剧中扮演的角色需要这种形象。当天，他的西装也是老式的款式，不合身，这也是为了更好地融入角色。', '["http://150.158.199.226:9000/avatar/aurora/talks/48f326e0b4246458c6f18ead713a209f.jpg"]', 0, 1, '2024-04-30 17:17:45', NULL),
	(71, 1, '最引人注目的还是文章 满头的白发，看起来十分苍老，要知道文章才仅仅38岁，正值青壮之年， 怎么也不至于白发。不过，有网友直言大概是染的，可能是话剧里的角色需要，正如当天，文章的西装也是老款式，十分不合身，也是衬托角色。', '["http://150.158.199.226:9000/avatar/aurora/talks/4aae0c758b71f0f8622231b5a8337ca3.jpg"]', 0, 1, '2024-04-30 17:18:05', NULL),
	(72, 1, '发布说说', '["http://150.158.199.226:9000/avatar/aurora/talks/640eebffec0abf58eff776e8bf3c3be8.jpg"]', 0, 1, '2024-05-06 21:14:03', NULL);
